module.exports = {"php_classes/ChatApplication.php":"<?php\n\nclass ChatApplication {\n\n    public function start() {\n        $socket = \\js_core::$window->io();\n        $jQuery = \\js_core::$jQuery ;\n        $this->bindButton($jQuery, $socket) ;\n        $this->bindSocket($socket) ;\n    }\n\n    private function bindButton($jQuery, $socket) {\n        $jQuery('#doSend')->on('click', function () use ($socket) {\n            $console = \\js_core::$console ;\n            $console->log('Clicked the #doSend span button') ;\n            self::submitForm($socket) ;\n        });\n    }\n\n    private function bindSocket($socket) {\n        $socket->on('chat message',  function ($msg) {\n            $console = \\js_core::$console ;\n            $console->log('Running the chat message event on the socket') ;\n            self::chatMessage($msg) ;\n        });\n    }\n\n    private function submitForm($socket) {\n        $console = \\js_core::$console ;\n        $jQuery = \\js_core::$jQuery ;\n        $current_message = $jQuery('#m')->val() ;\n        $console->log('current message is', $current_message) ;\n        $socket->emit('chat message', $current_message);\n        $jQuery('#m')->val('');\n    }\n\n    public static function chatMessage($msg) {\n        $console = \\js_core::$console ;\n        $jQuery = \\js_core::$jQuery ;\n        $html_to_append = '<li>'.$msg.'</li>' ;\n        $console->log('appending html', $html_to_append) ;\n        $jQuery('#messages')->append($html_to_append);\n        \\WindowMessage::showMessage('OMG That is so cool your message was sent', 'good') ;\n    }\n\n}","php_classes/WindowMessage.php":"<?php\n\nclass WindowMessage {\n\n    public static function showMessage($msg_data, $msg_type) {\n        $jQuery = \\js_core::$jQuery ;\n        $message_overlay = $jQuery('#message_overlay') ;\n        $msg_hash = self::makeid() ;\n        $message_overlay->append(self::getMessageHTML($msg_data, $msg_type, $msg_hash)) ;\n\n        $window = \\js_core::$window ;\n        $window->setTimeout(function() use ($msg_hash) {\n                self::hideMessage($msg_hash) ;\n            }, 3000) ;\n    }\n\n    public static function hideMessage($msg_hash) {\n        $jQuery = \\js_core::$jQuery ;\n        $message_overlay = $jQuery('#msg_'.$msg_hash) ;\n        $message_overlay->remove() ;\n        return true ;\n    }\n\n    public static function getMessageHTML($msg_data, $msg_type, $msg_hash) {\n        if ($msg_type === \"good\") {\n            $msg_class = ' btn-success' ; }\n        else {\n            $msg_class = ' btn-danger' ; }\n        $h = '' ;\n        $h = $h . '  <div id=\"msg_'.$msg_hash.'\" class=\"fullRow\">' ;\n        $h = $h . '    <span class=\"message_text '.$msg_class.'\">' ;\n        $h = $h . $msg_data ;\n        $h = $h . '    </span>' ;\n        $h = $h . '  </div>' ;\n        return $h ;\n    }\n\n    public static function makeid() {\n        $uuid = \\js_core::$window->require('uuid') ;\n        $console = \\js_core::$console ;\n        $random = $uuid->v4() ;\n        $console->log(\"random\", $random) ;\n        return $random;\n    }\n\n}","php_core/main_aaa.php":"<?php\n\n$console->log(\"PHP FE Started\") ;\n\\js_core::$jQuery = $jQuery ;\n\\js_core::$console = $console ;\n\\js_core::$window = $window ;\n\\js_core::$Math = $Math ;\n$console->log(\"js class initialised\") ;\n$console->log(\"socket aaa:\", $socket) ;\n\n$console->log(\"Before js core\") ;\n\n\nclass js_core {\n    public static $jQuery ;\n    public static $console ;\n    public static $window ;\n    public static $Math ;\n}\n\n$console->log(\"After js core\") ;\n\n$console->log(\"Before starting chat application\") ;\n\n$chat_application = new \\ChatApplication();\n$chat_application->start() ;\n\n$console->log(\"After starting chat application\") ;","php_core/main_window.php":"<?php"};